import {
  Button,
  Popover,
  PopoverBody,
  PopoverHeader,
  UncontrolledPopover,
  CloseButton,
  Badge,
} from "reactstrap";
import { useState, useContext } from "react";
import Cart from "./cart";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartShopping, faUser } from "@fortawesome/free-solid-svg-icons";
import AppContext from "./context";

export default function PopOver(props) {
  const [close, setClose] = useState(false);
  const { cart } = useContext(AppContext);

  return (
    <>
      <style jsx>
        {`
          a {
            color: white;
          }
          h5 {
            color: white;
            padding-top: 11px;
          }
          .cart-badge {
              position: relative,
              left: -8px;
              top: -10px;
              opacity: 0.9;
          }
        `}
      </style>
      <div>
        <FontAwesomeIcon
          icon={faCartShopping}
          className='mt-2'
          color='primary'
          id='popover644119284'
          type='button'
          onClick={() => setClose(true)}>
          <Badge className='cart-badge'>{props.total || 0}</Badge>{" "}
        </FontAwesomeIcon>
        {close && (
          <UncontrolledPopover
            placement='top'
            target='popover644119284'>
            <PopoverBody>
              <CloseButton onClick={(e) => setClose(false)} />
              <Cart />
            </PopoverBody>
          </UncontrolledPopover>
        )}
      </div>
    </>
  );
}
