  //       // setUser((user = res.data.user));
  //       // alert(`welcome, ${res.data.user.username}`);
  //       console.log(`DATA FROM STRAPPI ${JSON.stringify(res.data)}`);
  //     })
  //     .catch((error) => {
  //       setError(error);
  //       setLoading(false);
  //     });
  // }

  return (
    <Form>
      <FormGroup row>
        <Label
          for='exampleEmail'
          sm={2}>
          Email
        </Label>
        <p>{user.email}</p>
        <Col sm={10}>
          <Input
            onChange={(event) => onChange(event)}
            name='email'
            // placeholder={user.email}
            type='email'
          />
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label
          for='username'
          sm={2}>
          Email
        </Label>
        <Col sm={10}>
          <p>{user.username}</p>
          <Input
            onChange={(event) => onChange(event)}
            name='username'
            // placeholder={user.username}
            type='username'
          />
        </Col>
        <Button onSubmit={submitProfile()} />
      </FormGroup>
    </Form>
  );
}

