/* /pages/register.js */

import React, { useState } from "react";

import {
  Container,
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "reactstrap";
//library located at lib directory Auth.js
import { useAuth } from "../lib/auth2";
import { KnownFragmentNamesRule, printIntrospectionSchema } from "graphql";

const Register = () => {
  //initialize user object
  const [data, setData] = useState({ email: "", username: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [alert, setAlert] = useState(null);
  const { registerUser } = useAuth();

  const submitForm = () => {
    setLoading(true);
    //this will put it in strapi database
    registerUser(data.username, data.email, data.password)
      .then((res) => {
        // set authed user in global context object
        // appContext.setUser(res.data.user);
        setLoading(false);
        console.log(`registered user: ${JSON.stringify(res.data)}`);
        console.log(`register user: ${user}`);
      })
      .catch((error) => {
        console.log(`error in register: ${error}`);
        //setError(error.response.data);
        setLoading(false);
        setAlert(false);
      });
    return (
      <Alert
        color='info'
        isOpen={alert}>
        {alert ? "SUCCESS" : "FAIL"}
      </Alert>
    );
  };

  return (
    <Container>
      <Row>
        <Col
          sm='12'
          md={{ size: 5, offset: 3 }}>
          <div className='paper'>
            <div className='header'>
              <img src='http://localhost:1337/uploads/5a60a9d26a764e7cba1099d8b157b5e9.png' />
            </div>
            <section className='wrapper'>
              {Object.entries(error).length !== 0 &&
                error.constructor === Object &&
                error.message.map((error) => {
                  return (
                    <div
                      key={error.messages[0].id}
                      style={{ marginBottom: 10 }}>
                      <small style={{ color: "red" }}>
                        {error.messages[0].message}
                      </small>
                    </div>
                  );
                })}
              <Form>
                <fieldset disabled={loading}>
                  <FormGroup>
                    <Label>Username:</Label>
                    <Input
                      disabled={loading}
                      onChange={(e) =>
                        setData({ ...data, username: e.target.value })
                      }
                      value={data.username}
                      type='text'
                      name='username'
                      style={{ height: 50, fontSize: "1.2em" }}
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label>Email:</Label>
                    <Input
                      onChange={(e) =>
                        setData({ ...data, email: e.target.value })
                      }
                      value={data.email}
                      type='email'
                      name='email'
                      style={{ height: 50, fontSize: "1.2em" }}
                    />
                  </FormGroup>
                  <FormGroup style={{ marginBottom: 30 }}>
                    <Label>Password:</Label>
                    <Input
                      onChange={(e) =>
                        setData({ ...data, password: e.target.value })
                      }
                      value={data.password}
                      type='password'
                      name='password'
                      style={{ height: 50, fontSize: "1.2em" }}
                    />
                  </FormGroup>
                  <FormGroup>
                    <span>
                      <a href=''>
                        <small>Forgot Password?</small>
                      </a>
                    </span>
                    <Button
                      style={{ float: "right", width: 120 }}
                      color='primary'
                      disabled={loading}
                      onClick={(e) => submitForm()}>
                      {loading ? "Loading.." : "Submit"}
                    </Button>
                  </FormGroup>
                </fieldset>
              </Form>
            </section>
          </div>
        </Col>
      </Row>
      <style jsx>
        {`
          .paper {
            border: 1px solid lightgray;
            box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2),
              0px 1px 1px 0px rgba(0, 0, 0, 0.14),
              0px 2px 1px -1px rgba(0, 0, 0, 0.12);
            border-radius: 6px;
            margin-top: 90px;
          }
          .notification {
            color: #ab003c;
          }
          .header {
            width: 100%;
            height: 120px;
            background-color: #2196f3;
            margin-bottom: 30px;
            border-radius-top: 6px;
          }
          .wrapper {
            padding: 10px 30px 20px 30px !important;
          }
          a {
            color: blue !important;
          }
          img {
            margin: 15px 30px 10px 50px;
          }
        `}
      </style>
    </Container>
  );
};
export default Register;
