import React, { useState } from "react";
import Cart from "../components/cart";
import {
  ApolloProvider,
  ApolloClient,
  HttpLink,
  InMemoryCache,
} from "@apollo/client";
import RestaurantList from "../components/restaurantList";
import {
  InputGroup,
  InputGroupAddon,
  Input,
  Row,
  Col,
  Container,
} from "reactstrap";

function Home() {
  const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
  console.log(`URL: ${API_URL}`);
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });
  //the above variables do all the work to set up Apollo

  //TODO
  //CART SHOULD BE INSIDE A CARD
  //CART CARD RENDERS IN THE SECOND COLUNM OF LAYOUT
  //CART IS RESPONSIVE - IF ITEMS ARE ADDED EXPAND

  return (
    <ApolloProvider client={client}>
      <div className='search'>
        <h2> Local Restaurants</h2>
        <InputGroup>
          <InputGroupAddon addonType='append'> Search </InputGroupAddon>
          <Input
            onChange={(e) => setQuery(e.target.value.toLocaleLowerCase())}
            value={query}
          />
        </InputGroup>
        <br></br>
      </div>
      <Row
        xs='2'
        className='d-flex'>
        <Col
          className='bg-dark border align-self-left justify-content-stretch'
          xs='8'>
          <Container>
            <RestaurantList search={query} />
          </Container>
        </Col>
        <Col
          className='bg-light border align-self-right'
          xs='4'>
          <Cart />
        </Col>
      </Row>
    </ApolloProvider>
  );
}
export default Home;
