export async function getStaticPaths() {
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });
  const data = await client.query({
    query: gql`
      query:  
        restaurants {
          id
          name
          dishes {
            id
            name
            description
            price
            image {
              url
            }
          }
        }

    `,
  });

  console.log(data);
  return {
    paths: [],
    fallback: false,
  };
}
