import Dishes from "../../components/dishes";
import { useRouter } from "next/router";
import {
  ApolloClient,
  InMemoryCache,
  gql,
  ApolloProvider,
  HttpLink,
  useQuery,
} from "@apollo/client";

export default function Restaurant() {
  const router = useRouter();

  // const renderDishes = (restaurantID) => {
