  return (
    <>
      {/* <div>{router.query.restaurant}</div> */}
      <h1>Hello</h1>
      {/* <div>{renderDishes(restaurantID)}</div> */}
    </>
  );
}

export async function getStaticPaths() {
  const GET_RESTAURANT_DISHES = gql`
    query {
      restaurants {
        id
        name
        dishes {
          id
          name
          description
          price
          image {
            url
          }
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  let restaurant = data;

  console.log(data);
  console.log(`Query Data: ${JSON.stringify(restaurant)}`);
  return {
    paths: [],
    fallback: false,
  };
}
