import {
  Button,
  Popover,
  PopoverBody,
  PopoverHeader,
  UncontrolledPopover,
  CloseButton,
} from "reactstrap";
import { useState } from "react";
import Cart from "./cart";

export default function PopOver() {
  const [close, setClose] = useState(false);

  const handle = (e) => {
    setClose(true);
  };
  return (
    <div>
      <Button
        className=' mt-2'
        color='primary'
        id='popover644119284'
        type='button'
        onClick={(e) => handle(e)}>
        Popover on top
      </Button>
      {close && (
        <UncontrolledPopover
          placement='top'
          target='popover644119284'>
          <PopoverBody>
            <CloseButton onClick={(e) => setClose(false)} />
            <Cart />
          </PopoverBody>
        </UncontrolledPopover>
      )}
    </div>
  );
}
