import React, { useContext, useState } from "react";
import Cart from "../components/cart";
import {
  ApolloProvider,
  ApolloClient,
  HttpLink,
  InMemoryCache,
} from "@apollo/client";
import RestaurantList from "./restaurants/restaurantList";
import {
  InputGroup,
  InputGroupAddon,
  Input,
  Row,
  Col,
  Container,
} from "reactstrap";
import AppContext from "../components/context";

function Home() {
  const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
  const addItem = useContext(AppContext);
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });

  //the above variables do all the work to set up Apollo

  //TODO
  //CART SHOULD BE INSIDE A CARD
  //CART CARD RENDERS IN THE SECOND COLUNM OF LAYOUT
  //CART IS RESPONSIVE - IF ITEMS ARE ADDED EXPAND

  return (
    <>
      <ApolloProvider client={client}>
        <Container>
          <div className='search'>
            <h2> Local Restaurants</h2>
            <InputGroup>
              <InputGroupAddon addonType='append'> Search </InputGroupAddon>
              <Input
                onChange={(e) => setQuery(e.target.value.toLocaleLowerCase())}
                value={query}
              />
            </InputGroup>
            <br></br>
          </div>
        </Container>
        <Row>
          <Col
            className='bg-dark border'
            md='8'
            lg='8'
            xl='8'>
            <Container>
              <RestaurantList search={query} />
            </Container>
          </Col>
          <Col
            className='bg-light border'
            md='4'
            lg='4'
            xl='4'>
            {addItem.cart.items.length !== 0 && (
              <Row xs='8'>
                <Cart />
              </Row>
            )}
          </Col>
        </Row>
      </ApolloProvider>
    </>
  );
}
export default Home;
