/* /components/Layout.js */

import React, { useContext, useState } from "react";
import Head from "next/head";
import { useAuth } from "../lib/auth2";
import Cart from "../components/cart";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCartShopping, faUser } from "@fortawesome/free-solid-svg-icons";

//Styling
import {
  Collapse,
  Container,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  NavbarText,
  Badge,
} from "reactstrap";
import AppContext from "./context";
import PopOver from "../components/popOver";

const Layout = (props) => {
  const title = "Welcome to Nextjs";
  var { user, setUser, logout } = useAuth();
  const toggle = () => setIsOpen(!isOpen);
  const [isOpen, setIsOpen] = useState(false);
  const { cart, addItem } = useContext(AppContext);
  const [totalItems, setTotalItems] = useState(0);

  const getItems = cart.items.map((element) => {
    let temp = element.quantity;
    temp && setTotalItems(() => [totalItems, getItems]);
  });

  // const total = {
  //   total: [totalItems, ...getItems],
  //   grand: totalItems + total,
  // };

  // setTotalItems([totalItems, ...getItems]);
  console.log(`getItems = ${JSON.stringify(getItems)}`);
  // console.log(`total quantity ${total.grand}`);

  return (
    <div>
      <Head>
        <title>{title}</title>
        <meta charSet='utf-8' />
        <meta
          name='viewport'
          content='initial-scale=1.0, width=device-width'
        />
        <script src='https://js.stripe.com/v3' />
        <script
          src='https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/js/bootstrap.bundle.min.js'
          integrity='sha384-ka7Sk0Gln4gmtz2MlQnikT1wXgYsOg+OMhuP+IlRH9sENBO0LRn5q+8nbTov4+1p'
          crossOrigin='anonymous'></script>
      </Head>

      <style jsx>
        {`
          a {
            color: white;
          }
          h5 {
            color: white;
            padding-top: 11px;
          }
          .cart-badge {
              position: relative,
              left: -8px;
              top: -10px;
              opacity: 0.9;
          }
        `}
      </style>
      <Navbar className='navbar navbar-light navbar-expand-md'>
        <NavbarToggler onClick={toggle} />
        <NavbarBrand href='/'>
          <img
            alt='NP'
            src='logo.png'
            style={{
              height: 80,
              width: 100,
            }}
          />
        </NavbarBrand>

        {cart.items.length > 0 && (
          <>
            <PopOver />
            <Badge>{}</Badge>
          </>
        )}

        <Collapse
          isOpen={isOpen}
          navbar>
          <Nav
            className='me-auto'
            navbar>
            <NavItem>
              <NavLink href='/display'>Display</NavLink>
            </NavItem>
            <NavItem className='ml-auto'>
              {user ? (
                <h5>{user.username}</h5>
              ) : (
                <NavLink href='/register'>Sign Up</NavLink>
              )}
            </NavItem>
            <NavItem>
              {user && <NavLink href='/profile'>Profile</NavLink>}
            </NavItem>
            <NavItem>
              {user ? (
                <NavLink
                  href='/'
                  onClick={() => {
                    logout();
                    setUser(null);
                  }}>
                  Logout
                </NavLink>
              ) : (
                <NavLink href='/login'>Sing In </NavLink>
              )}
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>

      <Container fluid>{props.children}</Container>
    </div>
  );
};

export default Layout;
